import React from 'react';

export default function About(props) {
 let mystyle={
  color:props.mode==='dark'?'white':'#0b2243' ,
  backgroundColor: props.mode==='dark'?'#0b2243':'white',
  border:'1px solid',
  borderColor:'white'
 }


